    // 获取图片
    public function getPicture(Request $request)
    {
        $client = new GuzzleHttp\Client();

        // 五张高清图
        $imagesArr = explode(',', $request->get('images'));
        // 800*800
        $subPath = '123/'.date('mdHis');
        $path = base_path('public\\').$subPath.'/';
        if (! is_dir($path))
        {
            mkdir($path, 0777, true);
        }
        foreach ($imagesArr as $key=>$image)
        {
            $filename = explode('.', explode('/', $image)[count(explode('/', $image))-1]);
            $client->request('get', $image, ['verify' => false,'save_to' => $path.$filename[0].'.'.$filename[1]]);
        }

        // 商品详情图
        if ($request->get('detail') !== 'false'){
            $url = "https://h5api.m.taobao.com/h5/mtop.taobao.detail.getdesc/6.0/?jsv=2.5.1&appKey=12574478&t=1587568247042&sign=066f2d270f09227bfe0d36df52ffd6e7&api=mtop.taobao.detail.getdesc&v=6.0&isSec=0&ecode=0&AntiFlood=true&AntiCreep=true&H5Request=true&type=jsonp&dataType=jsonp&callback=mtopjsonp3&data=%7B%22id%22%3A%22".$request->get('nid')."%22%2C%22type%22%3A%220%22%7D";
            $res = $client->request('get', $url, ['verify'=>false]);
            preg_match('#("pages":\[.*?\])#is', (string)$res->getBody(), $match);
            $data = json_decode('{'.$match[1].'}', true);
            $list = [];
            foreach ($data['pages'] as $key=>$value)
            {
                preg_match('#size=(.*?)x(.*?)>#is', $value, $size);
                if(! empty($size) && $size[1] < $size[2]){
                    preg_match('#>(.*?)<#is', $value, $img);
                    array_push($list, $img[1]);
                }
            }
            foreach ($list as $key=>$value)
            {
                $filename = explode('.', explode('/', $value)[count(explode('/', $value))-1]);
                $client->request('get', $value, ['verify' => false,'save_to' => $path.'detail_'.$filename[0].'.'.$filename[1]]);
            }
        }
        // 修剪图片
        $this->reSize($path);
        $image_url = [];
        if ($dh = opendir($path)){
            while (($file = readdir($dh)) !== false){
                if ($file != '..' && $file != '.'){
                    array_push($image_url, $subPath.$file);
                }
            }
        }

        return ['message'=>'ok', 'image_url' => $image_url];
    }

    // 修剪图片 800*800 750*1000
    public function reSize($path)
    {
        if ($dh = opendir($path)){
            $i = 0;
            $j = 0;
            while(($file = readdir($dh)) !== false){
                if ($file != '..' && $file != '.'){
                    if (strpos($file, 'detail') !== false){
                        Image::make($path.$file)->resize(800, 800)->save($path.'detail_800x800_'.$i.'.'.$this->getDev($file));
                        Image::make($path.$file)->resize(750, 1000)->save($path.'detail_750x1000_'.$i.'.'.$this->getDev($file));
                        $i++;
                    }else{
                        Image::make($path.$file)->resize(800, 800)->save($path.'800x800_'.$j.'.'.$this->getDev($file));
                        Image::make($path.$file)->resize(750, 1000)->save($path.'750x1000_'.$j.'.'.$this->getDev($file));
                        $j++;
                    }
                    unlink($path.$file);
                }
            }
        }
    }

    // 获取后缀
    public function getDev($filename)
    {
        $arr = explode('.', $filename);
        return $arr[count($arr)-1];
    }

